CREATE PROCEDURE [dbo].[sp_Kirim_Dumas]
	@ID uniqueidentifier,
	@Sumber varchar(200),
    @Nomor varchar(200),
    @Email varchar(200),
    @Handphone varchar(255) = '',
    @TempatKejadian varchar(max),
    @Jenis_Pelanggaran varchar(max),
    @WaktuKejadian datetime,
    @Kronologi varchar(max),
    @Status varchar(50),
    @CreatedBy varchar(200)
AS

BEGIN

	DECLARE @Nama VARCHAR(200);

	SET @Nama = (SELECT Fullname FROM tblM_User WHERE Email = @Email);

	INSERT INTO [dbo].[tblT_Tanggapan]
           ([ID],[JenisPengaduan]
		  ,[IDPengaduan]
		  ,[TipePengirim]
		  ,[Email]
		  ,[Nama]
		  ,[Tanggapan]
		  ,[FileLampiran]
		  ,[FileLampiran_Ekstension]		  
		  ,[CreatedBy])
     VALUES
	 (
			@ID,
			@JenisPengaduan,
			@IDPengaduan,
			'Admin SPP',
			@Email,
			ISNULL(NULLIF(@Nama, ''), @Email),
			@Tanggapan,
			NULLIF(@FileLampiran, ''),
			NULLIF(@FileLampiran_Ekstension, ''),
			@CreatedBy
	 )

END

