-- =============================================
CREATE PROCEDURE [dbo].[sp_Save_User]
	-- Add the parameters for the stored procedure here
	@Action nvarchar(50),
	@UserID varchar(36),
	@PasswordHash varchar(max) = '',
	@Fullname varchar(100) = '',
	@Email varchar(100) = '',
	@Mobile varchar(50) = '',
	@Address varchar(max) = '',
	@Gender nvarchar(2) = '',	
	@NIP varchar(100) = '',
	@Jabatan varchar(200) = '',
	@Divisi varchar(200) = '',
	@ID_Roles varchar(max) = '',
	@Img nvarchar(100) = '',
	@Ekstension nvarchar(100) = '',
	@isActive int,
	@CreatedBy varchar(200)
AS

BEGIN -- SET NOCOUNT ON added to prevent extra result sets from -- interfering with SELECT statements. SET NOCOUNT ON;
IF(SELECT COUNT(*) FROM NotificationSetting) = 0 BEGIN
INSERT INTO [NotificationSetting] ( ID, SMTPAddress, SMTPPort, EmailAddress, Password, SenderName, EnableSSL, UseDefaultCredentials, UseAsync, EnableServices, CreatedBy,
NewUser, NewRoleAssignment, UserPasswordReset, Messaging, ReminderServices
) VALUES ( NEWID(), @SMTPAddress, @SMTPPort, @EmailAddress, @Password, @SenderName, @EnableSSL, @UseDefaultCredentials, @UseAsync, @EnableServices, @CreatedBy,
@NewUser, @NewRoleAssignment, @UserPasswordReset, @Messaging, @ReminderServices )
END ELSE BEGIN
UPDATE NotificationSetting SET SMTPAddress = @SMTPAddress, SMTPPort = @SMTPPort, EmailAddress = @EmailAddress, Password = @Password, SenderName = @SenderName, EnableSSL = @EnableSSL, UseDefaultCredentials = @UseDefaultCredentials, UseAsync = @UseAsync, EnableServices = @EnableServices,
NewUser = @NewUser, NewRoleAssignment = @NewRoleAssignment, UserPasswordReset = @UserPasswordReset, Messaging = @Messaging, ReminderServices = @ReminderServices, UpdatedBy = @CreatedBy, [UpdatedOn] = GETDATE() END
EXEC sp_RecordAuditTrail @CreatedBy, 'Administrator', 'Setting', 'Notification Setting', 'Update', 'Success' END

