DECLARE @Email_Audit VARCHAR(200) = (SELECT Email FROM tblM_User WHERE UserID = @UserID) EXEC sp_RecordAuditTrail @CreatedBy, 'My Profile', 'Password', NULL, 'UPDATE', @Email_Audit END

